# was generated by the Rails when you ran the scaffold generator.

describe AskingPricesController do

  def mock_asking_price(stubs={})
    @mock_asking_price ||= mock_model(AskingPrice, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all asking_prices as @asking_prices" do
      AskingPrice.stub(:all) { [mock_asking_price] }
      get :index
      assigns(:asking_prices).should eq([mock_asking_price])
    end
  end

  describe "GET show" do
    it "assigns the requested asking_price as @asking_price" do
      AskingPrice.stub(:find).with("37") { mock_asking_price }
      get :show, :id => "37"
      assigns(:asking_price).should be(mock_asking_price)
    end
  end

  describe "GET new" do
    it "assigns a new asking_price as @asking_price" do
      AskingPrice.stub(:new) { mock_asking_price }
      get :new
      assigns(:asking_price).should be(mock_asking_price)
    end
  end

  describe "GET edit" do
    it "assigns the requested asking_price as @asking_price" do
      AskingPrice.stub(:find).with("37") { mock_asking_price }
      get :edit, :id => "37"
      assigns(:asking_price).should be(mock_asking_price)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created asking_price as @asking_price" do
        AskingPrice.stub(:new).with({'these' => 'params'}) { mock_asking_price(:save => true) }
        post :create, :asking_price => {'these' => 'params'}
        assigns(:asking_price).should be(mock_asking_price)
      end

      it "redirects to the created asking_price" do
        AskingPrice.stub(:new) { mock_asking_price(:save => true) }
        post :create, :asking_price => {}
        response.should redirect_to(asking_price_url(mock_asking_price))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved asking_price as @asking_price" do
        AskingPrice.stub(:new).with({'these' => 'params'}) { mock_asking_price(:save => false) }
        post :create, :asking_price => {'these' => 'params'}
        assigns(:asking_price).should be(mock_asking_price)
      end

      it "re-renders the 'new' template" do
        AskingPrice.stub(:new) { mock_asking_price(:save => false) }
        post :create, :asking_price => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested asking_price" do
        AskingPrice.stub(:find).with("37") { mock_asking_price }
        mock_asking_price.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :asking_price => {'these' => 'params'}
      end

      it "assigns the requested asking_price as @asking_price" do
        AskingPrice.stub(:find) { mock_asking_price(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:asking_price).should be(mock_asking_price)
      end

      it "redirects to the asking_price" do
        AskingPrice.stub(:find) { mock_asking_price(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(asking_price_url(mock_asking_price))
      end
    end

    describe "with invalid params" do
      it "assigns the asking_price as @asking_price" do
        AskingPrice.stub(:find) { mock_asking_price(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:asking_price).should be(mock_asking_price)
      end

      it "re-renders the 'edit' template" do
        AskingPrice.stub(:find) { mock_asking_price(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested asking_price" do
      AskingPrice.stub(:find).with("37") { mock_asking_price }
      mock_asking_price.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the asking_prices list" do
      AskingPrice.stub(:find) { mock_asking_price }
      delete :destroy, :id => "1"
      response.should redirect_to(asking_prices_url)
    end
  end

end
