describe BoatColorsController do

  def mock_boat_color(stubs={})
    @mock_boat_color ||= mock_model(BoatColor, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all boat_colors as @boat_colors" do
      BoatColor.stub(:all) { [mock_boat_color] }
      get :index
      assigns(:boat_colors).should eq([mock_boat_color])
    end
  end

  describe "GET show" do
    it "assigns the requested boat_color as @boat_color" do
      BoatColor.stub(:find).with("37") { mock_boat_color }
      get :show, :id => "37"
      assigns(:boat_color).should be(mock_boat_color)
    end
  end

  describe "GET new" do
    it "assigns a new boat_color as @boat_color" do
      BoatColor.stub(:new) { mock_boat_color }
      get :new
      assigns(:boat_color).should be(mock_boat_color)
    end
  end

  describe "GET edit" do
    it "assigns the requested boat_color as @boat_color" do
      BoatColor.stub(:find).with("37") { mock_boat_color }
      get :edit, :id => "37"
      assigns(:boat_color).should be(mock_boat_color)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created boat_color as @boat_color" do
        BoatColor.stub(:new).with({'these' => 'params'}) { mock_boat_color(:save => true) }
        post :create, :boat_color => {'these' => 'params'}
        assigns(:boat_color).should be(mock_boat_color)
      end

      it "redirects to the created boat_color" do
        BoatColor.stub(:new) { mock_boat_color(:save => true) }
        post :create, :boat_color => {}
        response.should redirect_to(boat_color_url(mock_boat_color))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved boat_color as @boat_color" do
        BoatColor.stub(:new).with({'these' => 'params'}) { mock_boat_color(:save => false) }
        post :create, :boat_color => {'these' => 'params'}
        assigns(:boat_color).should be(mock_boat_color)
      end

      it "re-renders the 'new' template" do
        BoatColor.stub(:new) { mock_boat_color(:save => false) }
        post :create, :boat_color => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested boat_color" do
        BoatColor.stub(:find).with("37") { mock_boat_color }
        mock_boat_color.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :boat_color => {'these' => 'params'}
      end

      it "assigns the requested boat_color as @boat_color" do
        BoatColor.stub(:find) { mock_boat_color(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:boat_color).should be(mock_boat_color)
      end

      it "redirects to the boat_color" do
        BoatColor.stub(:find) { mock_boat_color(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(boat_color_url(mock_boat_color))
      end
    end

    describe "with invalid params" do
      it "assigns the boat_color as @boat_color" do
        BoatColor.stub(:find) { mock_boat_color(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:boat_color).should be(mock_boat_color)
      end

      it "re-renders the 'edit' template" do
        BoatColor.stub(:find) { mock_boat_color(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested boat_color" do
      BoatColor.stub(:find).with("37") { mock_boat_color }
      mock_boat_color.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the boat_colors list" do
      BoatColor.stub(:find) { mock_boat_color }
      delete :destroy, :id => "1"
      response.should redirect_to(boat_colors_url)
    end
