      it "assigns a newly created but unsaved boat_make as @boat_make" do
        BoatMake.stub(:new).with({'these' => 'params'}) { mock_boat_make(:save => false) }
        post :create, :boat_make => {'these' => 'params'}
        assigns(:boat_make).should be(mock_boat_make)
      end

      it "re-renders the 'new' template" do
        BoatMake.stub(:new) { mock_boat_make(:save => false) }
        post :create, :boat_make => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
