require 'spec_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to specify the controller code that
# was generated by the Rails when you ran the scaffold generator.

describe BoatModelsController do

  def mock_boat_model(stubs={})
    @mock_boat_model ||= mock_model(BoatModel, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all boat_models as @boat_models" do
      BoatModel.stub(:all) { [mock_boat_model] }
      get :index
      assigns(:boat_models).should eq([mock_boat_model])
    end
  end

  describe "GET show" do
    it "assigns the requested boat_model as @boat_model" do
      BoatModel.stub(:find).with("37") { mock_boat_model }
      get :show, :id => "37"
      assigns(:boat_model).should be(mock_boat_model)
    end
  end

  describe "GET new" do
    it "assigns a new boat_model as @boat_model" do
      BoatModel.stub(:new) { mock_boat_model }
      get :new
      assigns(:boat_model).should be(mock_boat_model)
    end
  end

  describe "GET edit" do
    it "assigns the requested boat_model as @boat_model" do
      BoatModel.stub(:find).with("37") { mock_boat_model }
      get :edit, :id => "37"
      assigns(:boat_model).should be(mock_boat_model)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created boat_model as @boat_model" do
        BoatModel.stub(:new).with({'these' => 'params'}) { mock_boat_model(:save => true) }
        post :create, :boat_model => {'these' => 'params'}
        assigns(:boat_model).should be(mock_boat_model)
      end

      it "redirects to the created boat_model" do
        BoatModel.stub(:new) { mock_boat_model(:save => true) }
        post :create, :boat_model => {}
        response.should redirect_to(boat_model_url(mock_boat_model))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved boat_model as @boat_model" do
        BoatModel.stub(:new).with({'these' => 'params'}) { mock_boat_model(:save => false) }
        post :create, :boat_model => {'these' => 'params'}
        assigns(:boat_model).should be(mock_boat_model)
      end

      it "re-renders the 'new' template" do
        BoatModel.stub(:new) { mock_boat_model(:save => false) }
        post :create, :boat_model => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested boat_model" do
        BoatModel.stub(:find).with("37") { mock_boat_model }
        mock_boat_model.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :boat_model => {'these' => 'params'}
      end

      it "assigns the requested boat_model as @boat_model" do
        BoatModel.stub(:find) { mock_boat_model(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:boat_model).should be(mock_boat_model)
      end

      it "redirects to the boat_model" do
        BoatModel.stub(:find) { mock_boat_model(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(boat_model_url(mock_boat_model))
      end
    end

    describe "with invalid params" do
      it "assigns the boat_model as @boat_model" do
        BoatModel.stub(:find) { mock_boat_model(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:boat_model).should be(mock_boat_model)
      end

      it "re-renders the 'edit' template" do
        BoatModel.stub(:find) { mock_boat_model(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested boat_model" do
      BoatModel.stub(:find).with("37") { mock_boat_model }
      mock_boat_model.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the boat_models list" do
      BoatModel.stub(:find) { mock_boat_model }
      delete :destroy, :id => "1"
      response.should redirect_to(boat_models_url)
    end
  end

end
