    @mock_boat_note ||= mock_model(BoatNote, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all boat_notes as @boat_notes" do
      BoatNote.stub(:all) { [mock_boat_note] }
      get :index
      assigns(:boat_notes).should eq([mock_boat_note])
    end
  end

  describe "GET show" do
    it "assigns the requested boat_note as @boat_note" do
      BoatNote.stub(:find).with("37") { mock_boat_note }
      get :show, :id => "37"
      assigns(:boat_note).should be(mock_boat_note)
    end
  end

  describe "GET new" do
    it "assigns a new boat_note as @boat_note" do
      BoatNote.stub(:new) { mock_boat_note }
      get :new
      assigns(:boat_note).should be(mock_boat_note)
    end
  end

  describe "GET edit" do
    it "assigns the requested boat_note as @boat_note" do
      BoatNote.stub(:find).with("37") { mock_boat_note }
      get :edit, :id => "37"
      assigns(:boat_note).should be(mock_boat_note)
    end
  end

  describe "POST create" do
