      it "assigns a newly created boat_note as @boat_note" do
        BoatNote.stub(:new).with({'these' => 'params'}) { mock_boat_note(:save => true) }
        post :create, :boat_note => {'these' => 'params'}
        assigns(:boat_note).should be(mock_boat_note)
      end

      it "redirects to the created boat_note" do
        BoatNote.stub(:new) { mock_boat_note(:save => true) }
        post :create, :boat_note => {}
        response.should redirect_to(boat_note_url(mock_boat_note))
      end
    end

