      it "assigns a newly created but unsaved boat_note as @boat_note" do
        BoatNote.stub(:new).with({'these' => 'params'}) { mock_boat_note(:save => false) }
        post :create, :boat_note => {'these' => 'params'}
        assigns(:boat_note).should be(mock_boat_note)
      end

      it "re-renders the 'new' template" do
        BoatNote.stub(:new) { mock_boat_note(:save => false) }
        post :create, :boat_note => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested boat_note" do
        BoatNote.stub(:find).with("37") { mock_boat_note }
        mock_boat_note.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :boat_note => {'these' => 'params'}
      end

      it "assigns the requested boat_note as @boat_note" do
        BoatNote.stub(:find) { mock_boat_note(:update_attributes => true) }
