    end

    describe "with invalid params" do
      it "assigns the boat_note as @boat_note" do
        BoatNote.stub(:find) { mock_boat_note(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:boat_note).should be(mock_boat_note)
      end

      it "re-renders the 'edit' template" do
        BoatNote.stub(:find) { mock_boat_note(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
