require 'spec_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to specify the controller code that
# was generated by the Rails when you ran the scaffold generator.

describe BoatPostsController do

  def mock_boat_post(stubs={})
    @mock_boat_post ||= mock_model(BoatPost, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all boat_posts as @boat_posts" do
      BoatPost.stub(:all) { [mock_boat_post] }
      get :index
      assigns(:boat_posts).should eq([mock_boat_post])
    end
  end

  describe "GET show" do
    it "assigns the requested boat_post as @boat_post" do
      BoatPost.stub(:find).with("37") { mock_boat_post }
      get :show, :id => "37"
      assigns(:boat_post).should be(mock_boat_post)
    end
  end

  describe "GET new" do
    it "assigns a new boat_post as @boat_post" do
      BoatPost.stub(:new) { mock_boat_post }
      get :new
      assigns(:boat_post).should be(mock_boat_post)
    end
  end

  describe "GET edit" do
    it "assigns the requested boat_post as @boat_post" do
      BoatPost.stub(:find).with("37") { mock_boat_post }
      get :edit, :id => "37"
      assigns(:boat_post).should be(mock_boat_post)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created boat_post as @boat_post" do
        BoatPost.stub(:new).with({'these' => 'params'}) { mock_boat_post(:save => true) }
        post :create, :boat_post => {'these' => 'params'}
        assigns(:boat_post).should be(mock_boat_post)
      end

      it "redirects to the created boat_post" do
        BoatPost.stub(:new) { mock_boat_post(:save => true) }
        post :create, :boat_post => {}
        response.should redirect_to(boat_post_url(mock_boat_post))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved boat_post as @boat_post" do
        BoatPost.stub(:new).with({'these' => 'params'}) { mock_boat_post(:save => false) }
        post :create, :boat_post => {'these' => 'params'}
        assigns(:boat_post).should be(mock_boat_post)
      end

      it "re-renders the 'new' template" do
        BoatPost.stub(:new) { mock_boat_post(:save => false) }
        post :create, :boat_post => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested boat_post" do
        BoatPost.stub(:find).with("37") { mock_boat_post }
        mock_boat_post.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :boat_post => {'these' => 'params'}
      end

      it "assigns the requested boat_post as @boat_post" do
        BoatPost.stub(:find) { mock_boat_post(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:boat_post).should be(mock_boat_post)
      end

      it "redirects to the boat_post" do
        BoatPost.stub(:find) { mock_boat_post(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(boat_post_url(mock_boat_post))
      end
    end

    describe "with invalid params" do
      it "assigns the boat_post as @boat_post" do
        BoatPost.stub(:find) { mock_boat_post(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:boat_post).should be(mock_boat_post)
      end

      it "re-renders the 'edit' template" do
        BoatPost.stub(:find) { mock_boat_post(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested boat_post" do
      BoatPost.stub(:find).with("37") { mock_boat_post }
      mock_boat_post.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the boat_posts list" do
      BoatPost.stub(:find) { mock_boat_post }
      delete :destroy, :id => "1"
      response.should redirect_to(boat_posts_url)
    end
  end

end
