  describe "GET show" do
    it "assigns the requested boat_user_name as @boat_user_name" do
      BoatUserName.stub(:find).with("37") { mock_boat_user_name }
      get :show, :id => "37"
      assigns(:boat_user_name).should be(mock_boat_user_name)
    end
  end

  describe "GET new" do
    it "assigns a new boat_user_name as @boat_user_name" do
      BoatUserName.stub(:new) { mock_boat_user_name }
      get :new
      assigns(:boat_user_name).should be(mock_boat_user_name)
    end
  end

  describe "GET edit" do
    it "assigns the requested boat_user_name as @boat_user_name" do
      BoatUserName.stub(:find).with("37") { mock_boat_user_name }
      get :edit, :id => "37"
