        post :create, :boat_user_name => {'these' => 'params'}
        assigns(:boat_user_name).should be(mock_boat_user_name)
      end

      it "redirects to the created boat_user_name" do
        BoatUserName.stub(:new) { mock_boat_user_name(:save => true) }
        post :create, :boat_user_name => {}
        response.should redirect_to(boat_user_name_url(mock_boat_user_name))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved boat_user_name as @boat_user_name" do
        BoatUserName.stub(:new).with({'these' => 'params'}) { mock_boat_user_name(:save => false) }
        post :create, :boat_user_name => {'these' => 'params'}
        assigns(:boat_user_name).should be(mock_boat_user_name)
      end

      it "re-renders the 'new' template" do
        BoatUserName.stub(:new) { mock_boat_user_name(:save => false) }
        post :create, :boat_user_name => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
