      it "assigns the requested boat_user_name as @boat_user_name" do
        BoatUserName.stub(:find) { mock_boat_user_name(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:boat_user_name).should be(mock_boat_user_name)
      end

      it "redirects to the boat_user_name" do
        BoatUserName.stub(:find) { mock_boat_user_name(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(boat_user_name_url(mock_boat_user_name))
      end
    end

    describe "with invalid params" do
      it "assigns the boat_user_name as @boat_user_name" do
        BoatUserName.stub(:find) { mock_boat_user_name(:update_attributes => false) }
