describe BoatUserPhonesController do

  def mock_boat_user_phone(stubs={})
    @mock_boat_user_phone ||= mock_model(BoatUserPhone, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all boat_user_phones as @boat_user_phones" do
      BoatUserPhone.stub(:all) { [mock_boat_user_phone] }
      get :index
      assigns(:boat_user_phones).should eq([mock_boat_user_phone])
    end
  end

  describe "GET show" do
    it "assigns the requested boat_user_phone as @boat_user_phone" do
      BoatUserPhone.stub(:find).with("37") { mock_boat_user_phone }
      get :show, :id => "37"
      assigns(:boat_user_phone).should be(mock_boat_user_phone)
    end
  end

  describe "GET new" do
    it "assigns a new boat_user_phone as @boat_user_phone" do
      BoatUserPhone.stub(:new) { mock_boat_user_phone }
      get :new
      assigns(:boat_user_phone).should be(mock_boat_user_phone)
    end
  end

  describe "GET edit" do
    it "assigns the requested boat_user_phone as @boat_user_phone" do
      BoatUserPhone.stub(:find).with("37") { mock_boat_user_phone }
      get :edit, :id => "37"
      assigns(:boat_user_phone).should be(mock_boat_user_phone)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created boat_user_phone as @boat_user_phone" do
        BoatUserPhone.stub(:new).with({'these' => 'params'}) { mock_boat_user_phone(:save => true) }
        post :create, :boat_user_phone => {'these' => 'params'}
        assigns(:boat_user_phone).should be(mock_boat_user_phone)
      end

      it "redirects to the created boat_user_phone" do
        BoatUserPhone.stub(:new) { mock_boat_user_phone(:save => true) }
        post :create, :boat_user_phone => {}
        response.should redirect_to(boat_user_phone_url(mock_boat_user_phone))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved boat_user_phone as @boat_user_phone" do
        BoatUserPhone.stub(:new).with({'these' => 'params'}) { mock_boat_user_phone(:save => false) }
        post :create, :boat_user_phone => {'these' => 'params'}
        assigns(:boat_user_phone).should be(mock_boat_user_phone)
      end

      it "re-renders the 'new' template" do
        BoatUserPhone.stub(:new) { mock_boat_user_phone(:save => false) }
        post :create, :boat_user_phone => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested boat_user_phone" do
        BoatUserPhone.stub(:find).with("37") { mock_boat_user_phone }
        mock_boat_user_phone.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :boat_user_phone => {'these' => 'params'}
      end

      it "assigns the requested boat_user_phone as @boat_user_phone" do
        BoatUserPhone.stub(:find) { mock_boat_user_phone(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:boat_user_phone).should be(mock_boat_user_phone)
      end

      it "redirects to the boat_user_phone" do
        BoatUserPhone.stub(:find) { mock_boat_user_phone(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(boat_user_phone_url(mock_boat_user_phone))
      end
    end

    describe "with invalid params" do
      it "assigns the boat_user_phone as @boat_user_phone" do
        BoatUserPhone.stub(:find) { mock_boat_user_phone(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:boat_user_phone).should be(mock_boat_user_phone)
      end

      it "re-renders the 'edit' template" do
        BoatUserPhone.stub(:find) { mock_boat_user_phone(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested boat_user_phone" do
      BoatUserPhone.stub(:find).with("37") { mock_boat_user_phone }
      mock_boat_user_phone.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the boat_user_phones list" do
      BoatUserPhone.stub(:find) { mock_boat_user_phone }
      delete :destroy, :id => "1"
      response.should redirect_to(boat_user_phones_url)
    end
  end

end
