describe CarInformationsController do

  def mock_car_information(stubs={})
    @mock_car_information ||= mock_model(CarInformation, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all car_informations as @car_informations" do
      CarInformation.stub(:all) { [mock_car_information] }
      get :index
      assigns(:car_informations).should eq([mock_car_information])
    end
  end

  describe "GET show" do
    it "assigns the requested car_information as @car_information" do
      CarInformation.stub(:find).with("37") { mock_car_information }
      get :show, :id => "37"
      assigns(:car_information).should be(mock_car_information)
    end
  end

  describe "GET new" do
    it "assigns a new car_information as @car_information" do
      CarInformation.stub(:new) { mock_car_information }
      get :new
      assigns(:car_information).should be(mock_car_information)
    end
  end

  describe "GET edit" do
    it "assigns the requested car_information as @car_information" do
      CarInformation.stub(:find).with("37") { mock_car_information }
      get :edit, :id => "37"
      assigns(:car_information).should be(mock_car_information)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created car_information as @car_information" do
        CarInformation.stub(:new).with({'these' => 'params'}) { mock_car_information(:save => true) }
        post :create, :car_information => {'these' => 'params'}
        assigns(:car_information).should be(mock_car_information)
      end

      it "redirects to the created car_information" do
        CarInformation.stub(:new) { mock_car_information(:save => true) }
        post :create, :car_information => {}
        response.should redirect_to(car_information_url(mock_car_information))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved car_information as @car_information" do
        CarInformation.stub(:new).with({'these' => 'params'}) { mock_car_information(:save => false) }
        post :create, :car_information => {'these' => 'params'}
        assigns(:car_information).should be(mock_car_information)
      end

      it "re-renders the 'new' template" do
        CarInformation.stub(:new) { mock_car_information(:save => false) }
        post :create, :car_information => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested car_information" do
        CarInformation.stub(:find).with("37") { mock_car_information }
        mock_car_information.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :car_information => {'these' => 'params'}
      end

      it "assigns the requested car_information as @car_information" do
        CarInformation.stub(:find) { mock_car_information(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:car_information).should be(mock_car_information)
      end

      it "redirects to the car_information" do
        CarInformation.stub(:find) { mock_car_information(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(car_information_url(mock_car_information))
      end
    end

    describe "with invalid params" do
      it "assigns the car_information as @car_information" do
        CarInformation.stub(:find) { mock_car_information(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:car_information).should be(mock_car_information)
      end

      it "re-renders the 'edit' template" do
        CarInformation.stub(:find) { mock_car_information(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested car_information" do
      CarInformation.stub(:find).with("37") { mock_car_information }
      mock_car_information.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the car_informations list" do
      CarInformation.stub(:find) { mock_car_information }
      delete :destroy, :id => "1"
      response.should redirect_to(car_informations_url)
    end
  end

end
