require 'spec_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to specify the controller code that
# was generated by the Rails when you ran the scaffold generator.

describe CarPowersController do

  def mock_car_power(stubs={})
    @mock_car_power ||= mock_model(CarPower, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all car_powers as @car_powers" do
      CarPower.stub(:all) { [mock_car_power] }
      get :index
      assigns(:car_powers).should eq([mock_car_power])
    end
  end

  describe "GET show" do
    it "assigns the requested car_power as @car_power" do
      CarPower.stub(:find).with("37") { mock_car_power }
      get :show, :id => "37"
      assigns(:car_power).should be(mock_car_power)
    end
  end

  describe "GET new" do
    it "assigns a new car_power as @car_power" do
      CarPower.stub(:new) { mock_car_power }
      get :new
      assigns(:car_power).should be(mock_car_power)
    end
  end

  describe "GET edit" do
    it "assigns the requested car_power as @car_power" do
      CarPower.stub(:find).with("37") { mock_car_power }
      get :edit, :id => "37"
      assigns(:car_power).should be(mock_car_power)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created car_power as @car_power" do
        CarPower.stub(:new).with({'these' => 'params'}) { mock_car_power(:save => true) }
        post :create, :car_power => {'these' => 'params'}
        assigns(:car_power).should be(mock_car_power)
      end

      it "redirects to the created car_power" do
        CarPower.stub(:new) { mock_car_power(:save => true) }
        post :create, :car_power => {}
        response.should redirect_to(car_power_url(mock_car_power))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved car_power as @car_power" do
        CarPower.stub(:new).with({'these' => 'params'}) { mock_car_power(:save => false) }
        post :create, :car_power => {'these' => 'params'}
        assigns(:car_power).should be(mock_car_power)
      end

      it "re-renders the 'new' template" do
        CarPower.stub(:new) { mock_car_power(:save => false) }
        post :create, :car_power => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested car_power" do
        CarPower.stub(:find).with("37") { mock_car_power }
        mock_car_power.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :car_power => {'these' => 'params'}
      end

      it "assigns the requested car_power as @car_power" do
        CarPower.stub(:find) { mock_car_power(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:car_power).should be(mock_car_power)
      end

      it "redirects to the car_power" do
        CarPower.stub(:find) { mock_car_power(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(car_power_url(mock_car_power))
      end
    end

    describe "with invalid params" do
      it "assigns the car_power as @car_power" do
        CarPower.stub(:find) { mock_car_power(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:car_power).should be(mock_car_power)
      end

      it "re-renders the 'edit' template" do
        CarPower.stub(:find) { mock_car_power(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested car_power" do
      CarPower.stub(:find).with("37") { mock_car_power }
      mock_car_power.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the car_powers list" do
      CarPower.stub(:find) { mock_car_power }
      delete :destroy, :id => "1"
      response.should redirect_to(car_powers_url)
    end
  end

end
