require 'spec_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to specify the controller code that
# was generated by the Rails when you ran the scaffold generator.

describe CarPricesController do

  def mock_car_price(stubs={})
    @mock_car_price ||= mock_model(CarPrice, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all car_prices as @car_prices" do
      CarPrice.stub(:all) { [mock_car_price] }
      get :index
      assigns(:car_prices).should eq([mock_car_price])
    end
  end

  describe "GET show" do
    it "assigns the requested car_price as @car_price" do
      CarPrice.stub(:find).with("37") { mock_car_price }
      get :show, :id => "37"
      assigns(:car_price).should be(mock_car_price)
    end
  end

  describe "GET new" do
    it "assigns a new car_price as @car_price" do
      CarPrice.stub(:new) { mock_car_price }
      get :new
      assigns(:car_price).should be(mock_car_price)
    end
  end

  describe "GET edit" do
    it "assigns the requested car_price as @car_price" do
      CarPrice.stub(:find).with("37") { mock_car_price }
      get :edit, :id => "37"
      assigns(:car_price).should be(mock_car_price)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created car_price as @car_price" do
        CarPrice.stub(:new).with({'these' => 'params'}) { mock_car_price(:save => true) }
        post :create, :car_price => {'these' => 'params'}
        assigns(:car_price).should be(mock_car_price)
      end

      it "redirects to the created car_price" do
        CarPrice.stub(:new) { mock_car_price(:save => true) }
        post :create, :car_price => {}
        response.should redirect_to(car_price_url(mock_car_price))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved car_price as @car_price" do
        CarPrice.stub(:new).with({'these' => 'params'}) { mock_car_price(:save => false) }
        post :create, :car_price => {'these' => 'params'}
        assigns(:car_price).should be(mock_car_price)
      end

      it "re-renders the 'new' template" do
        CarPrice.stub(:new) { mock_car_price(:save => false) }
        post :create, :car_price => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested car_price" do
        CarPrice.stub(:find).with("37") { mock_car_price }
        mock_car_price.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :car_price => {'these' => 'params'}
      end

      it "assigns the requested car_price as @car_price" do
        CarPrice.stub(:find) { mock_car_price(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:car_price).should be(mock_car_price)
      end

      it "redirects to the car_price" do
        CarPrice.stub(:find) { mock_car_price(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(car_price_url(mock_car_price))
      end
    end

    describe "with invalid params" do
      it "assigns the car_price as @car_price" do
        CarPrice.stub(:find) { mock_car_price(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:car_price).should be(mock_car_price)
      end

      it "re-renders the 'edit' template" do
        CarPrice.stub(:find) { mock_car_price(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested car_price" do
      CarPrice.stub(:find).with("37") { mock_car_price }
      mock_car_price.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the car_prices list" do
      CarPrice.stub(:find) { mock_car_price }
      delete :destroy, :id => "1"
      response.should redirect_to(car_prices_url)
    end
  end

end
