describe CarTopSpeedsController do

  def mock_car_top_speed(stubs={})
    @mock_car_top_speed ||= mock_model(CarTopSpeed, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all car_top_speeds as @car_top_speeds" do
      CarTopSpeed.stub(:all) { [mock_car_top_speed] }
      get :index
      assigns(:car_top_speeds).should eq([mock_car_top_speed])
    end
  end

  describe "GET show" do
    it "assigns the requested car_top_speed as @car_top_speed" do
      CarTopSpeed.stub(:find).with("37") { mock_car_top_speed }
      get :show, :id => "37"
      assigns(:car_top_speed).should be(mock_car_top_speed)
    end
  end

  describe "GET new" do
    it "assigns a new car_top_speed as @car_top_speed" do
      CarTopSpeed.stub(:new) { mock_car_top_speed }
      get :new
      assigns(:car_top_speed).should be(mock_car_top_speed)
    end
  end

  describe "GET edit" do
    it "assigns the requested car_top_speed as @car_top_speed" do
      CarTopSpeed.stub(:find).with("37") { mock_car_top_speed }
      get :edit, :id => "37"
      assigns(:car_top_speed).should be(mock_car_top_speed)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created car_top_speed as @car_top_speed" do
        CarTopSpeed.stub(:new).with({'these' => 'params'}) { mock_car_top_speed(:save => true) }
        post :create, :car_top_speed => {'these' => 'params'}
        assigns(:car_top_speed).should be(mock_car_top_speed)
      end

      it "redirects to the created car_top_speed" do
        CarTopSpeed.stub(:new) { mock_car_top_speed(:save => true) }
        post :create, :car_top_speed => {}
        response.should redirect_to(car_top_speed_url(mock_car_top_speed))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved car_top_speed as @car_top_speed" do
        CarTopSpeed.stub(:new).with({'these' => 'params'}) { mock_car_top_speed(:save => false) }
        post :create, :car_top_speed => {'these' => 'params'}
        assigns(:car_top_speed).should be(mock_car_top_speed)
      end

      it "re-renders the 'new' template" do
        CarTopSpeed.stub(:new) { mock_car_top_speed(:save => false) }
        post :create, :car_top_speed => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested car_top_speed" do
        CarTopSpeed.stub(:find).with("37") { mock_car_top_speed }
        mock_car_top_speed.should_receive(:update_attributes).with({'these' => 'params'})
