      end

      it "assigns the requested car_top_speed as @car_top_speed" do
        CarTopSpeed.stub(:find) { mock_car_top_speed(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:car_top_speed).should be(mock_car_top_speed)
      end

      it "redirects to the car_top_speed" do
        CarTopSpeed.stub(:find) { mock_car_top_speed(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(car_top_speed_url(mock_car_top_speed))
      end
    end

    describe "with invalid params" do
      it "assigns the car_top_speed as @car_top_speed" do
        CarTopSpeed.stub(:find) { mock_car_top_speed(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:car_top_speed).should be(mock_car_top_speed)
      end

