  describe "GET show" do
    it "assigns the requested car_user_email as @car_user_email" do
      CarUserEmail.stub(:find).with("37") { mock_car_user_email }
      get :show, :id => "37"
      assigns(:car_user_email).should be(mock_car_user_email)
    end
  end

  describe "GET new" do
    it "assigns a new car_user_email as @car_user_email" do
      CarUserEmail.stub(:new) { mock_car_user_email }
      get :new
      assigns(:car_user_email).should be(mock_car_user_email)
    end
  end

  describe "GET edit" do
    it "assigns the requested car_user_email as @car_user_email" do
      CarUserEmail.stub(:find).with("37") { mock_car_user_email }
