        post :create, :car_user_email => {'these' => 'params'}
        assigns(:car_user_email).should be(mock_car_user_email)
      end

      it "redirects to the created car_user_email" do
        CarUserEmail.stub(:new) { mock_car_user_email(:save => true) }
        post :create, :car_user_email => {}
        response.should redirect_to(car_user_email_url(mock_car_user_email))
      end
    end

