      it "assigns a newly created but unsaved car_user_email as @car_user_email" do
        CarUserEmail.stub(:new).with({'these' => 'params'}) { mock_car_user_email(:save => false) }
        post :create, :car_user_email => {'these' => 'params'}
        assigns(:car_user_email).should be(mock_car_user_email)
      end

      it "re-renders the 'new' template" do
        CarUserEmail.stub(:new) { mock_car_user_email(:save => false) }
        post :create, :car_user_email => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
