        CarUserEmail.stub(:find) { mock_car_user_email(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:car_user_email).should be(mock_car_user_email)
      end

      it "redirects to the car_user_email" do
        CarUserEmail.stub(:find) { mock_car_user_email(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(car_user_email_url(mock_car_user_email))
      end
    end

    describe "with invalid params" do
      it "assigns the car_user_email as @car_user_email" do
