describe CarUserNamesController do

  def mock_car_user_name(stubs={})
    @mock_car_user_name ||= mock_model(CarUserName, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all car_user_names as @car_user_names" do
      CarUserName.stub(:all) { [mock_car_user_name] }
      get :index
      assigns(:car_user_names).should eq([mock_car_user_name])
    end
  end

  describe "GET show" do
    it "assigns the requested car_user_name as @car_user_name" do
      CarUserName.stub(:find).with("37") { mock_car_user_name }
      get :show, :id => "37"
      assigns(:car_user_name).should be(mock_car_user_name)
    end
  end

  describe "GET new" do
    it "assigns a new car_user_name as @car_user_name" do
      CarUserName.stub(:new) { mock_car_user_name }
      get :new
      assigns(:car_user_name).should be(mock_car_user_name)
    end
  end

  describe "GET edit" do
    it "assigns the requested car_user_name as @car_user_name" do
      CarUserName.stub(:find).with("37") { mock_car_user_name }
      get :edit, :id => "37"
      assigns(:car_user_name).should be(mock_car_user_name)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created car_user_name as @car_user_name" do
        CarUserName.stub(:new).with({'these' => 'params'}) { mock_car_user_name(:save => true) }
        post :create, :car_user_name => {'these' => 'params'}
        assigns(:car_user_name).should be(mock_car_user_name)
      end

      it "redirects to the created car_user_name" do
        CarUserName.stub(:new) { mock_car_user_name(:save => true) }
        post :create, :car_user_name => {}
        response.should redirect_to(car_user_name_url(mock_car_user_name))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved car_user_name as @car_user_name" do
        CarUserName.stub(:new).with({'these' => 'params'}) { mock_car_user_name(:save => false) }
        post :create, :car_user_name => {'these' => 'params'}
        assigns(:car_user_name).should be(mock_car_user_name)
      end

      it "re-renders the 'new' template" do
        CarUserName.stub(:new) { mock_car_user_name(:save => false) }
        post :create, :car_user_name => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested car_user_name" do
        CarUserName.stub(:find).with("37") { mock_car_user_name }
        mock_car_user_name.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :car_user_name => {'these' => 'params'}
      end

      it "assigns the requested car_user_name as @car_user_name" do
        CarUserName.stub(:find) { mock_car_user_name(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:car_user_name).should be(mock_car_user_name)
      end

      it "redirects to the car_user_name" do
        CarUserName.stub(:find) { mock_car_user_name(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(car_user_name_url(mock_car_user_name))
      end
    end

    describe "with invalid params" do
      it "assigns the car_user_name as @car_user_name" do
        CarUserName.stub(:find) { mock_car_user_name(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:car_user_name).should be(mock_car_user_name)
      end

      it "re-renders the 'edit' template" do
        CarUserName.stub(:find) { mock_car_user_name(:update_attributes => false) }
        put :update, :id => "1"
