describe CaravansController do

  def mock_caravan(stubs={})
    @mock_caravan ||= mock_model(Caravan, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all caravans as @caravans" do
      Caravan.stub(:all) { [mock_caravan] }
      get :index
      assigns(:caravans).should eq([mock_caravan])
    end
  end

  describe "GET show" do
    it "assigns the requested caravan as @caravan" do
      Caravan.stub(:find).with("37") { mock_caravan }
      get :show, :id => "37"
      assigns(:caravan).should be(mock_caravan)
    end
  end

  describe "GET new" do
    it "assigns a new caravan as @caravan" do
      Caravan.stub(:new) { mock_caravan }
      get :new
      assigns(:caravan).should be(mock_caravan)
    end
  end

  describe "GET edit" do
    it "assigns the requested caravan as @caravan" do
      Caravan.stub(:find).with("37") { mock_caravan }
      get :edit, :id => "37"
      assigns(:caravan).should be(mock_caravan)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created caravan as @caravan" do
        Caravan.stub(:new).with({'these' => 'params'}) { mock_caravan(:save => true) }
        post :create, :caravan => {'these' => 'params'}
        assigns(:caravan).should be(mock_caravan)
      end

      it "redirects to the created caravan" do
        Caravan.stub(:new) { mock_caravan(:save => true) }
        post :create, :caravan => {}
        response.should redirect_to(caravan_url(mock_caravan))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved caravan as @caravan" do
        Caravan.stub(:new).with({'these' => 'params'}) { mock_caravan(:save => false) }
        post :create, :caravan => {'these' => 'params'}
        assigns(:caravan).should be(mock_caravan)
      end

      it "re-renders the 'new' template" do
        Caravan.stub(:new) { mock_caravan(:save => false) }
        post :create, :caravan => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested caravan" do
        Caravan.stub(:find).with("37") { mock_caravan }
        mock_caravan.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :caravan => {'these' => 'params'}
      end

      it "assigns the requested caravan as @caravan" do
        Caravan.stub(:find) { mock_caravan(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:caravan).should be(mock_caravan)
      end

      it "redirects to the caravan" do
        Caravan.stub(:find) { mock_caravan(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(caravan_url(mock_caravan))
      end
    end

    describe "with invalid params" do
      it "assigns the caravan as @caravan" do
        Caravan.stub(:find) { mock_caravan(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:caravan).should be(mock_caravan)
      end

      it "re-renders the 'edit' template" do
        Caravan.stub(:find) { mock_caravan(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested caravan" do
      Caravan.stub(:find).with("37") { mock_caravan }
      mock_caravan.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the caravans list" do
      Caravan.stub(:find) { mock_caravan }
      delete :destroy, :id => "1"
      response.should redirect_to(caravans_url)
    end
  end

end
