      assigns(:fuel_type).should be(mock_fuel_type)
    end
  end

  describe "GET edit" do
    it "assigns the requested fuel_type as @fuel_type" do
      FuelType.stub(:find).with("37") { mock_fuel_type }
      get :edit, :id => "37"
      assigns(:fuel_type).should be(mock_fuel_type)
    end
  end

