      it "assigns a newly created fuel_type as @fuel_type" do
        FuelType.stub(:new).with({'these' => 'params'}) { mock_fuel_type(:save => true) }
        post :create, :fuel_type => {'these' => 'params'}
        assigns(:fuel_type).should be(mock_fuel_type)
      end

      it "redirects to the created fuel_type" do
        FuelType.stub(:new) { mock_fuel_type(:save => true) }
        post :create, :fuel_type => {}
        response.should redirect_to(fuel_type_url(mock_fuel_type))
      end
    end

