      it "assigns a newly created but unsaved fuel_type as @fuel_type" do
        FuelType.stub(:new).with({'these' => 'params'}) { mock_fuel_type(:save => false) }
        post :create, :fuel_type => {'these' => 'params'}
        assigns(:fuel_type).should be(mock_fuel_type)
      end

      it "re-renders the 'new' template" do
        FuelType.stub(:new) { mock_fuel_type(:save => false) }
        post :create, :fuel_type => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
