        mock_fuel_type.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :fuel_type => {'these' => 'params'}
      end

      it "assigns the requested fuel_type as @fuel_type" do
        FuelType.stub(:find) { mock_fuel_type(:update_attributes => true) }
