      it "assigns the fuel_type as @fuel_type" do
        FuelType.stub(:find) { mock_fuel_type(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:fuel_type).should be(mock_fuel_type)
      end

      it "re-renders the 'edit' template" do
        FuelType.stub(:find) { mock_fuel_type(:update_attributes => false) }
