require 'spec_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to specify the controller code that
# was generated by the Rails when you ran the scaffold generator.

describe HeightsController do

  def mock_height(stubs={})
    @mock_height ||= mock_model(Height, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all heights as @heights" do
      Height.stub(:all) { [mock_height] }
      get :index
      assigns(:heights).should eq([mock_height])
    end
  end

  describe "GET show" do
    it "assigns the requested height as @height" do
      Height.stub(:find).with("37") { mock_height }
      get :show, :id => "37"
      assigns(:height).should be(mock_height)
    end
  end

  describe "GET new" do
    it "assigns a new height as @height" do
      Height.stub(:new) { mock_height }
      get :new
      assigns(:height).should be(mock_height)
    end
  end

  describe "GET edit" do
    it "assigns the requested height as @height" do
      Height.stub(:find).with("37") { mock_height }
      get :edit, :id => "37"
      assigns(:height).should be(mock_height)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created height as @height" do
        Height.stub(:new).with({'these' => 'params'}) { mock_height(:save => true) }
        post :create, :height => {'these' => 'params'}
        assigns(:height).should be(mock_height)
      end

      it "redirects to the created height" do
        Height.stub(:new) { mock_height(:save => true) }
        post :create, :height => {}
        response.should redirect_to(height_url(mock_height))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved height as @height" do
        Height.stub(:new).with({'these' => 'params'}) { mock_height(:save => false) }
        post :create, :height => {'these' => 'params'}
        assigns(:height).should be(mock_height)
      end

      it "re-renders the 'new' template" do
        Height.stub(:new) { mock_height(:save => false) }
        post :create, :height => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested height" do
        Height.stub(:find).with("37") { mock_height }
        mock_height.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :height => {'these' => 'params'}
      end

      it "assigns the requested height as @height" do
        Height.stub(:find) { mock_height(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:height).should be(mock_height)
      end

      it "redirects to the height" do
        Height.stub(:find) { mock_height(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(height_url(mock_height))
      end
    end

    describe "with invalid params" do
      it "assigns the height as @height" do
        Height.stub(:find) { mock_height(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:height).should be(mock_height)
      end

      it "re-renders the 'edit' template" do
        Height.stub(:find) { mock_height(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested height" do
      Height.stub(:find).with("37") { mock_height }
      mock_height.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the heights list" do
      Height.stub(:find) { mock_height }
      delete :destroy, :id => "1"
      response.should redirect_to(heights_url)
    end
  end

end
