require 'spec_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to specify the controller code that
# was generated by the Rails when you ran the scaffold generator.

describe LengthsController do

  def mock_length(stubs={})
    @mock_length ||= mock_model(Length, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all lengths as @lengths" do
      Length.stub(:all) { [mock_length] }
      get :index
      assigns(:lengths).should eq([mock_length])
    end
  end

  describe "GET show" do
    it "assigns the requested length as @length" do
      Length.stub(:find).with("37") { mock_length }
      get :show, :id => "37"
      assigns(:length).should be(mock_length)
    end
  end

  describe "GET new" do
    it "assigns a new length as @length" do
      Length.stub(:new) { mock_length }
      get :new
      assigns(:length).should be(mock_length)
    end
  end

  describe "GET edit" do
    it "assigns the requested length as @length" do
      Length.stub(:find).with("37") { mock_length }
      get :edit, :id => "37"
      assigns(:length).should be(mock_length)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created length as @length" do
        Length.stub(:new).with({'these' => 'params'}) { mock_length(:save => true) }
        post :create, :length => {'these' => 'params'}
        assigns(:length).should be(mock_length)
      end

      it "redirects to the created length" do
        Length.stub(:new) { mock_length(:save => true) }
        post :create, :length => {}
        response.should redirect_to(length_url(mock_length))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved length as @length" do
        Length.stub(:new).with({'these' => 'params'}) { mock_length(:save => false) }
        post :create, :length => {'these' => 'params'}
        assigns(:length).should be(mock_length)
      end

      it "re-renders the 'new' template" do
        Length.stub(:new) { mock_length(:save => false) }
        post :create, :length => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested length" do
        Length.stub(:find).with("37") { mock_length }
        mock_length.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :length => {'these' => 'params'}
      end

      it "assigns the requested length as @length" do
        Length.stub(:find) { mock_length(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:length).should be(mock_length)
      end

      it "redirects to the length" do
        Length.stub(:find) { mock_length(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(length_url(mock_length))
      end
    end

    describe "with invalid params" do
      it "assigns the length as @length" do
        Length.stub(:find) { mock_length(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:length).should be(mock_length)
      end

      it "re-renders the 'edit' template" do
        Length.stub(:find) { mock_length(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested length" do
      Length.stub(:find).with("37") { mock_length }
      mock_length.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the lengths list" do
      Length.stub(:find) { mock_length }
      delete :destroy, :id => "1"
      response.should redirect_to(lengths_url)
    end
  end

end
