require 'spec_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to specify the controller code that
# was generated by the Rails when you ran the scaffold generator.

describe MachinePostsController do

  def mock_machine_post(stubs={})
    @mock_machine_post ||= mock_model(MachinePost, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all machine_posts as @machine_posts" do
      MachinePost.stub(:all) { [mock_machine_post] }
      get :index
      assigns(:machine_posts).should eq([mock_machine_post])
    end
  end

  describe "GET show" do
    it "assigns the requested machine_post as @machine_post" do
      MachinePost.stub(:find).with("37") { mock_machine_post }
      get :show, :id => "37"
      assigns(:machine_post).should be(mock_machine_post)
    end
  end

  describe "GET new" do
    it "assigns a new machine_post as @machine_post" do
      MachinePost.stub(:new) { mock_machine_post }
      get :new
      assigns(:machine_post).should be(mock_machine_post)
    end
  end

  describe "GET edit" do
    it "assigns the requested machine_post as @machine_post" do
      MachinePost.stub(:find).with("37") { mock_machine_post }
      get :edit, :id => "37"
      assigns(:machine_post).should be(mock_machine_post)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created machine_post as @machine_post" do
        MachinePost.stub(:new).with({'these' => 'params'}) { mock_machine_post(:save => true) }
        post :create, :machine_post => {'these' => 'params'}
        assigns(:machine_post).should be(mock_machine_post)
      end

      it "redirects to the created machine_post" do
        MachinePost.stub(:new) { mock_machine_post(:save => true) }
        post :create, :machine_post => {}
        response.should redirect_to(machine_post_url(mock_machine_post))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved machine_post as @machine_post" do
        MachinePost.stub(:new).with({'these' => 'params'}) { mock_machine_post(:save => false) }
        post :create, :machine_post => {'these' => 'params'}
        assigns(:machine_post).should be(mock_machine_post)
      end

      it "re-renders the 'new' template" do
        MachinePost.stub(:new) { mock_machine_post(:save => false) }
        post :create, :machine_post => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested machine_post" do
        MachinePost.stub(:find).with("37") { mock_machine_post }
        mock_machine_post.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :machine_post => {'these' => 'params'}
      end

      it "assigns the requested machine_post as @machine_post" do
        MachinePost.stub(:find) { mock_machine_post(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:machine_post).should be(mock_machine_post)
      end

      it "redirects to the machine_post" do
        MachinePost.stub(:find) { mock_machine_post(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(machine_post_url(mock_machine_post))
      end
    end

    describe "with invalid params" do
      it "assigns the machine_post as @machine_post" do
        MachinePost.stub(:find) { mock_machine_post(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:machine_post).should be(mock_machine_post)
      end

      it "re-renders the 'edit' template" do
        MachinePost.stub(:find) { mock_machine_post(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested machine_post" do
      MachinePost.stub(:find).with("37") { mock_machine_post }
      mock_machine_post.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the machine_posts list" do
      MachinePost.stub(:find) { mock_machine_post }
      delete :destroy, :id => "1"
      response.should redirect_to(machine_posts_url)
    end
  end

end
