describe MotoMakesController do

  def mock_moto_make(stubs={})
    @mock_moto_make ||= mock_model(MotoMake, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all moto_makes as @moto_makes" do
      MotoMake.stub(:all) { [mock_moto_make] }
      get :index
      assigns(:moto_makes).should eq([mock_moto_make])
    end
  end

  describe "GET show" do
    it "assigns the requested moto_make as @moto_make" do
      MotoMake.stub(:find).with("37") { mock_moto_make }
      get :show, :id => "37"
      assigns(:moto_make).should be(mock_moto_make)
    end
  end

  describe "GET new" do
    it "assigns a new moto_make as @moto_make" do
      MotoMake.stub(:new) { mock_moto_make }
      get :new
      assigns(:moto_make).should be(mock_moto_make)
    end
  end

  describe "GET edit" do
    it "assigns the requested moto_make as @moto_make" do
      MotoMake.stub(:find).with("37") { mock_moto_make }
      get :edit, :id => "37"
      assigns(:moto_make).should be(mock_moto_make)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created moto_make as @moto_make" do
        MotoMake.stub(:new).with({'these' => 'params'}) { mock_moto_make(:save => true) }
        post :create, :moto_make => {'these' => 'params'}
        assigns(:moto_make).should be(mock_moto_make)
      end

      it "redirects to the created moto_make" do
        MotoMake.stub(:new) { mock_moto_make(:save => true) }
        post :create, :moto_make => {}
        response.should redirect_to(moto_make_url(mock_moto_make))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved moto_make as @moto_make" do
        MotoMake.stub(:new).with({'these' => 'params'}) { mock_moto_make(:save => false) }
        post :create, :moto_make => {'these' => 'params'}
        assigns(:moto_make).should be(mock_moto_make)
      end

      it "re-renders the 'new' template" do
        MotoMake.stub(:new) { mock_moto_make(:save => false) }
        post :create, :moto_make => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested moto_make" do
        MotoMake.stub(:find).with("37") { mock_moto_make }
        mock_moto_make.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :moto_make => {'these' => 'params'}
      end

      it "assigns the requested moto_make as @moto_make" do
        MotoMake.stub(:find) { mock_moto_make(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:moto_make).should be(mock_moto_make)
      end

      it "redirects to the moto_make" do
        MotoMake.stub(:find) { mock_moto_make(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(moto_make_url(mock_moto_make))
      end
    end

    describe "with invalid params" do
      it "assigns the moto_make as @moto_make" do
        MotoMake.stub(:find) { mock_moto_make(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:moto_make).should be(mock_moto_make)
      end

      it "re-renders the 'edit' template" do
        MotoMake.stub(:find) { mock_moto_make(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested moto_make" do
      MotoMake.stub(:find).with("37") { mock_moto_make }
      mock_moto_make.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the moto_makes list" do
      MotoMake.stub(:find) { mock_moto_make }
      delete :destroy, :id => "1"
      response.should redirect_to(moto_makes_url)
    end
  end

end
