require 'spec_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to specify the controller code that
# was generated by the Rails when you ran the scaffold generator.

describe MotoModelSectionsController do

  def mock_moto_model_section(stubs={})
    @mock_moto_model_section ||= mock_model(MotoModelSection, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all moto_model_sections as @moto_model_sections" do
      MotoModelSection.stub(:all) { [mock_moto_model_section] }
      get :index
      assigns(:moto_model_sections).should eq([mock_moto_model_section])
    end
  end

  describe "GET show" do
    it "assigns the requested moto_model_section as @moto_model_section" do
      MotoModelSection.stub(:find).with("37") { mock_moto_model_section }
      get :show, :id => "37"
      assigns(:moto_model_section).should be(mock_moto_model_section)
    end
  end

  describe "GET new" do
    it "assigns a new moto_model_section as @moto_model_section" do
      MotoModelSection.stub(:new) { mock_moto_model_section }
      get :new
      assigns(:moto_model_section).should be(mock_moto_model_section)
    end
  end

  describe "GET edit" do
    it "assigns the requested moto_model_section as @moto_model_section" do
      MotoModelSection.stub(:find).with("37") { mock_moto_model_section }
      get :edit, :id => "37"
      assigns(:moto_model_section).should be(mock_moto_model_section)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created moto_model_section as @moto_model_section" do
        MotoModelSection.stub(:new).with({'these' => 'params'}) { mock_moto_model_section(:save => true) }
        post :create, :moto_model_section => {'these' => 'params'}
        assigns(:moto_model_section).should be(mock_moto_model_section)
      end

      it "redirects to the created moto_model_section" do
        MotoModelSection.stub(:new) { mock_moto_model_section(:save => true) }
        post :create, :moto_model_section => {}
        response.should redirect_to(moto_model_section_url(mock_moto_model_section))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved moto_model_section as @moto_model_section" do
        MotoModelSection.stub(:new).with({'these' => 'params'}) { mock_moto_model_section(:save => false) }
        post :create, :moto_model_section => {'these' => 'params'}
        assigns(:moto_model_section).should be(mock_moto_model_section)
      end

      it "re-renders the 'new' template" do
        MotoModelSection.stub(:new) { mock_moto_model_section(:save => false) }
        post :create, :moto_model_section => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested moto_model_section" do
        MotoModelSection.stub(:find).with("37") { mock_moto_model_section }
        mock_moto_model_section.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :moto_model_section => {'these' => 'params'}
      end

      it "assigns the requested moto_model_section as @moto_model_section" do
        MotoModelSection.stub(:find) { mock_moto_model_section(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:moto_model_section).should be(mock_moto_model_section)
      end

      it "redirects to the moto_model_section" do
        MotoModelSection.stub(:find) { mock_moto_model_section(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(moto_model_section_url(mock_moto_model_section))
      end
    end

    describe "with invalid params" do
      it "assigns the moto_model_section as @moto_model_section" do
        MotoModelSection.stub(:find) { mock_moto_model_section(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:moto_model_section).should be(mock_moto_model_section)
      end

      it "re-renders the 'edit' template" do
        MotoModelSection.stub(:find) { mock_moto_model_section(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested moto_model_section" do
      MotoModelSection.stub(:find).with("37") { mock_moto_model_section }
      mock_moto_model_section.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the moto_model_sections list" do
      MotoModelSection.stub(:find) { mock_moto_model_section }
      delete :destroy, :id => "1"
      response.should redirect_to(moto_model_sections_url)
    end
  end

end
