require 'spec_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to specify the controller code that
# was generated by the Rails when you ran the scaffold generator.

describe VehicleClassesController do

  def mock_vehicle_class(stubs={})
    @mock_vehicle_class ||= mock_model(VehicleClass, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all vehicle_classes as @vehicle_classes" do
      VehicleClass.stub(:all) { [mock_vehicle_class] }
      get :index
      assigns(:vehicle_classes).should eq([mock_vehicle_class])
    end
  end

  describe "GET show" do
    it "assigns the requested vehicle_class as @vehicle_class" do
      VehicleClass.stub(:find).with("37") { mock_vehicle_class }
      get :show, :id => "37"
      assigns(:vehicle_class).should be(mock_vehicle_class)
    end
  end

  describe "GET new" do
    it "assigns a new vehicle_class as @vehicle_class" do
      VehicleClass.stub(:new) { mock_vehicle_class }
      get :new
      assigns(:vehicle_class).should be(mock_vehicle_class)
    end
  end

  describe "GET edit" do
    it "assigns the requested vehicle_class as @vehicle_class" do
      VehicleClass.stub(:find).with("37") { mock_vehicle_class }
      get :edit, :id => "37"
      assigns(:vehicle_class).should be(mock_vehicle_class)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created vehicle_class as @vehicle_class" do
        VehicleClass.stub(:new).with({'these' => 'params'}) { mock_vehicle_class(:save => true) }
        post :create, :vehicle_class => {'these' => 'params'}
        assigns(:vehicle_class).should be(mock_vehicle_class)
      end

      it "redirects to the created vehicle_class" do
        VehicleClass.stub(:new) { mock_vehicle_class(:save => true) }
        post :create, :vehicle_class => {}
        response.should redirect_to(vehicle_class_url(mock_vehicle_class))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved vehicle_class as @vehicle_class" do
        VehicleClass.stub(:new).with({'these' => 'params'}) { mock_vehicle_class(:save => false) }
        post :create, :vehicle_class => {'these' => 'params'}
        assigns(:vehicle_class).should be(mock_vehicle_class)
      end

      it "re-renders the 'new' template" do
        VehicleClass.stub(:new) { mock_vehicle_class(:save => false) }
        post :create, :vehicle_class => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested vehicle_class" do
        VehicleClass.stub(:find).with("37") { mock_vehicle_class }
        mock_vehicle_class.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :vehicle_class => {'these' => 'params'}
      end

      it "assigns the requested vehicle_class as @vehicle_class" do
        VehicleClass.stub(:find) { mock_vehicle_class(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:vehicle_class).should be(mock_vehicle_class)
      end

      it "redirects to the vehicle_class" do
        VehicleClass.stub(:find) { mock_vehicle_class(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(vehicle_class_url(mock_vehicle_class))
      end
    end

    describe "with invalid params" do
      it "assigns the vehicle_class as @vehicle_class" do
        VehicleClass.stub(:find) { mock_vehicle_class(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:vehicle_class).should be(mock_vehicle_class)
      end

      it "re-renders the 'edit' template" do
        VehicleClass.stub(:find) { mock_vehicle_class(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested vehicle_class" do
      VehicleClass.stub(:find).with("37") { mock_vehicle_class }
      mock_vehicle_class.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the vehicle_classes list" do
      VehicleClass.stub(:find) { mock_vehicle_class }
      delete :destroy, :id => "1"
      response.should redirect_to(vehicle_classes_url)
    end
  end

end
