require 'spec_helper'

# This spec was generated by rspec-rails when you ran the scaffold generator.
# It demonstrates how one might use RSpec to specify the controller code that
# was generated by the Rails when you ran the scaffold generator.

describe WindowsController do

  def mock_window(stubs={})
    @mock_window ||= mock_model(Window, stubs).as_null_object
  end

  describe "GET index" do
    it "assigns all windows as @windows" do
      Window.stub(:all) { [mock_window] }
      get :index
      assigns(:windows).should eq([mock_window])
    end
  end

  describe "GET show" do
    it "assigns the requested window as @window" do
      Window.stub(:find).with("37") { mock_window }
      get :show, :id => "37"
      assigns(:window).should be(mock_window)
    end
  end

  describe "GET new" do
    it "assigns a new window as @window" do
      Window.stub(:new) { mock_window }
      get :new
      assigns(:window).should be(mock_window)
    end
  end

  describe "GET edit" do
    it "assigns the requested window as @window" do
      Window.stub(:find).with("37") { mock_window }
      get :edit, :id => "37"
      assigns(:window).should be(mock_window)
    end
  end

  describe "POST create" do
    describe "with valid params" do
      it "assigns a newly created window as @window" do
        Window.stub(:new).with({'these' => 'params'}) { mock_window(:save => true) }
        post :create, :window => {'these' => 'params'}
        assigns(:window).should be(mock_window)
      end

      it "redirects to the created window" do
        Window.stub(:new) { mock_window(:save => true) }
        post :create, :window => {}
        response.should redirect_to(window_url(mock_window))
      end
    end

    describe "with invalid params" do
      it "assigns a newly created but unsaved window as @window" do
        Window.stub(:new).with({'these' => 'params'}) { mock_window(:save => false) }
        post :create, :window => {'these' => 'params'}
        assigns(:window).should be(mock_window)
      end

      it "re-renders the 'new' template" do
        Window.stub(:new) { mock_window(:save => false) }
        post :create, :window => {}
        response.should render_template("new")
      end
    end
  end

  describe "PUT update" do
    describe "with valid params" do
      it "updates the requested window" do
        Window.stub(:find).with("37") { mock_window }
        mock_window.should_receive(:update_attributes).with({'these' => 'params'})
        put :update, :id => "37", :window => {'these' => 'params'}
      end

      it "assigns the requested window as @window" do
        Window.stub(:find) { mock_window(:update_attributes => true) }
        put :update, :id => "1"
        assigns(:window).should be(mock_window)
      end

      it "redirects to the window" do
        Window.stub(:find) { mock_window(:update_attributes => true) }
        put :update, :id => "1"
        response.should redirect_to(window_url(mock_window))
      end
    end

    describe "with invalid params" do
      it "assigns the window as @window" do
        Window.stub(:find) { mock_window(:update_attributes => false) }
        put :update, :id => "1"
        assigns(:window).should be(mock_window)
      end

      it "re-renders the 'edit' template" do
        Window.stub(:find) { mock_window(:update_attributes => false) }
        put :update, :id => "1"
        response.should render_template("edit")
      end
    end
  end

  describe "DELETE destroy" do
    it "destroys the requested window" do
      Window.stub(:find).with("37") { mock_window }
      mock_window.should_receive(:destroy)
      delete :destroy, :id => "37"
    end

    it "redirects to the windows list" do
      Window.stub(:find) { mock_window }
      delete :destroy, :id => "1"
      response.should redirect_to(windows_url)
    end
  end

end
